/**
 * A generic utility for tracking a metric across scopes.
 */
export class Budget {
    amount = 0;
    constructor(initial) {
        this.amount = initial;
    }
    static unlimited() {
        return new Budget(Infinity);
    }
    isUnlimited() {
        return this.amount === Infinity;
    }
    remaining() {
        return this.amount;
    }
    spend(amount) {
        this.amount -= amount;
    }
}
