/** Checks if a value is a plain JavaScript object (POJO). */
export declare function isPOJO(value: any): boolean;
/** Deeply clones a plain JavaScript object. */
export declare function deepClone<T extends Record<any, any> | Array<any>>(object: T): T;
/** Deeply checks if two plain JavaScript objects are equal. */
export declare function isEqual(obj1: any, obj2: any): boolean;
